actor_test3:

actor_test3_VRAM_size_b		equ 0x80	; VRAM size to alloc (size of largest frame, bytes)

;==============================================================
; Actor: test3 Sprite: test3
;==============================================================
actor_test3_sprite_test3:
	dc.l spritesheet_test3_test3
	dc.l actor_test3_sheet_test3_frame_0_subsprite_dimensions_bits
	dc.l actor_test3_sheet_test3_frame_0_subsprite_pos_offsets
	dc.l palette_test3
	dc.w (actor_test3_sheet_test3_width<<8)|actor_test3_sheet_test3_height
	dc.w actor_test3_VRAM_size_b
	dc.w actor_test3_sheet_test3_frame_0_size_t
	dc.w actor_test3_sheet_test3_frame_0_size_subsprites
	dc.b 0x0
	dc.b 0x0
;==============================================================

actor_test3_sheet_test3_width		equ 0x10	; Max width of largest frame in pixels
actor_test3_sheet_test3_height		equ 0x10	; Max height of largest frame in pixels


actor_test3_sheet_test3_frametable:
actor_test3_sheet_test3_frame_0_header:
	dc.w 0x0004	; Frame size (tiles)
	dc.w actor_test3_sheet_test3_frame_0_size_subsprites	; Frame size (subsprites)
	dc.l actor_test3_sheet_test3_frame_0
	dc.l actor_test3_sheet_test3_frame_0_subsprite_dimensions_bits
	dc.l actor_test3_sheet_test3_frame_0_subsprite_pos_offsets

actor_test3_sheet_test3_frame_0_size_b		equ 0x0080	; Size in bytes
actor_test3_sheet_test3_frame_0_size_t		equ 0x0004	; Size in tiles
actor_test3_sheet_test3_frame_0_size_subsprites		equ 0x0001	; Size in subsprites

; Subsprite offsets from 0,0 (in pixels) - unflipped (ww) and flipped X (ww)
actor_test3_sheet_test3_frame_0_subsprite_pos_offsets:
	dc.l 0x00000000, 0x00000000
	Even

; Subsprite dimension bits (for sprite descs)
actor_test3_sheet_test3_frame_0_subsprite_dimensions_bits:
	dc.b 0x5
	Even


